#!/usr/bin/octave -qfif (nargin!=7)printf("Usage: ldaexperiment.m <trdata> <trlabels> <tedata> <telabels> <mink> <stepk> <maxk>\n")exit(1);end;arg_list=argv();trdata=arg_list{1};trlabs=arg_list{2};tedata=arg_list{3};telabs=arg_list{4};mink=str2num(arg_list{5});stepk=str2num(arg_list{6});maxk=str2num(arg_list{7});#Cargamos las matricesload(trdata);load(trlabs);load(tedata);load(telabs);#calculamos la proyecci√≥n LDAW = lda(X,xl);for k = mink:stepk:maxk  vectorLDA = W(:,1:k)';  XR = vectorLDA*X;  YR = vectorLDA*Y;  err = knn(XR,xl,YR,yl,1);  printf("%d\t%.3f\n", k, err); end;