function[m,W]=pca(X)  n = columns(X); #El número de columnas equivale al número de muestras  #Calculamos el vector media por columnas  m = sum(X,2) / n;  #Restar a todos los datos el vector media  matrizResta = X -m;  #calcular matriz de covarianza  matcovarianza = 1/n*matrizResta*matrizResta';  #calculamos los vectores y valores propios [V, lambda] = eig (A) V --> vectores propios. lambda--> valores propios  [V, lambda] = eig(matcovarianza);  #ordenar los vectores propios segun su valor propio descendiente  [values,vindicesordenacion] = sort(diag(lambda), "descend"); # diag me permite obtener el vector diagonal  para ordenar  W=V(:,vindicesordenacion);endfunction