#!/usr/bin/octave -qfif (nargin!=10)printf("Usage: pcaexp.m <trdata> <trlabels> <tedata> <telabels> <mink> <stepk> <maxk> <mink2> <stepk2> <maxk2>\n")exit(1);end;arg_list=argv();trdata=arg_list{1};trlabs=arg_list{2};tedata=arg_list{3};telabs=arg_list{4};mink=str2num(arg_list{5});stepk=str2num(arg_list{6});maxk=str2num(arg_list{7});mink2=str2num(arg_list{8});stepk2=str2num(arg_list{9});maxk2=str2num(arg_list{10});#Cargamos las matricesload(trdata);load(trlabs);load(tedata);load(telabs);[m,W] = pca(X);dataToTrain = X-m;dataToTest = Y-m;for i = mink:stepk:maxk  vectorPCA = W(:,1:i)';  XR = vectorPCA*dataToTrain;  V = lda(XR,xl);  for k = mink2:stepk2:maxk2    vectorLDA = V(:,1:k)';    XRR = vectorLDA*XR;    YRR = vectorLDA*vectorPCA*(Y-m);    err = knn(XRR,xl,YRR,yl,1);    printf("PCA i= %d\t lda k = %d \t error =%.3f\n", i,k, err); end;end;