#Tabla dentistaT = [0 0 0 .576; 0 0 1 .008; 0 1 0 .144; 0 1 1 .072;      1 0 0 .064; 1 0 1 .012; 1 1 0 .016; 1 1 1 .108];#Elemento en la fila 1 columna 4T(1,4)#Elemento de la fila 1 ultima columna T(1,end)#Elementos de la fila 1 a la 4 ultima columnaT(1:4,end)#Elementos todas las filas ultima columnaT(:,end)#Elementos en las filas 1, 2, 5, 6 ultima columnaT([1,2,5,6],end)#suma de todas las probabilidadessum(T(:,end))#Indicadores de filas sin huecoT(:,3) == 0#filas con caries (elementos dos no nulos)find(T(:,2))#filas sin caries y sin hueco (2 y 3 nulos)find(T(:,2)==0 & T(:,3)==0)#Elementos con 2 y 3 nulos --> probabilidadesT(find(T(:,2)==0 & T(:,3)==0),end)#Probabilidad de caries y hueco a la vezPch1 = sum(T(find(T(:,2) & T(:,3)),end))#Probabilidad de huecoPh1 = sum(T(find(T(:,3)),end))#Probabilidad de carias sabiendo que hay huecoPc1Dh1 = Pch1 /Ph1#Probabilidad de dolor sabiendo que hay caries Pd1c1 = sum(T(find(T(:,1) & T(:,2)),end))Pc1  = sum(T(find(T(:,2)),end))Pdc = Pd1c1 / Pc1#EjercicioPd = sum(T(find(T(:,1)),end))Pc1d1 = Pc1 * Pdc/PdPp = Pd1c1 /Pd